mapper.select:
/* SQL_ID : role.selectRoleList */ select wr.* , (select count(*) from w_role_admin where role_no = wr.role_no) as r_cnt, wa1.admin_id as create_id, wa1.admin_name as create_nm, wa2.admin_id as update_id, wa2.admin_name as update_name from w_role wr join w_admin wa1 on(wr.create_no = wa1.admin_no) left join w_admin wa2 on(wr.update_no = wa2.admin_no) where 1=1
and name like '%${role_keyword}%'
order by wr.create_date desc

/* SQL_ID : role.selectAbcRoleList */ select wr.* , (select count(*) from w_abc_role_admin where role_no = wr.role_no) as r_cnt, wa1.admin_id as create_id, wa1.admin_name as create_nm, wa2.admin_id as update_id, wa2.admin_name as update_name from w_abc_role wr join w_admin wa1 on(wr.create_no = wa1.admin_no) left join w_admin wa2 on(wr.update_no = wa2.admin_no) where 1=1
and name like '%${role_keyword}%'
order by wr.create_date desc

mapper.insert:
/* SQL_ID : role.insertRole */
SELECT MAX(role_no) + 1 as role_no FROM w_abc_role
insert into w_abc_role( role_no, name, create_no, create_date ) values( #{role_no}, #{name}, #{admin_no}, now() )

mapper.update:
    	 /* SQL_ID : role.insertRole */ 
    	update w_abc_role set
			name = #{name},
			update_no = #{admin_no},
			update_date= now()
		where role_no = #{role_no}
